                        "Content-Type": "application/json"
                    },
                    body: JSON.stringify(newTask)
                });
            } else {
                response = await fetch("http://localhost:4000/api/activities", {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json"
                    },
                    body: JSON.stringify(newTask)
                });
