import React, { useState } from "react";

function Formlogin (props) {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch("http://localhost:4000/api/users/login", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({email, password}),
            });
            const data = await response.json();
            if (response.ok) {
                localStorage.setItem("token", data.token);
                window.location.href= "/diary";
            } else {
                alert(data.message);
            }
        } catch (error) {
            console.error("Error during login:", error);
            alert("An error occurred. Please try again later.");
        }
    };
    return(
        <div className="content-login">
            <form className="form-login">
                <p> { props.message }  </p>
                <label className="label-user" htmlFor = "email"> { props.email } </label>
                <input className="in-email" name="email" type="text" placeholder="User email" />
                <label className="label-password" for="password"> { props.pass } </label>
                <input className="in-password" name="password" type="password" placeholder="User Password"/>
                <button className="login-button" type="submit"> {props.login} </button>
                <p> { props.messageReg } </p>
                <button className="register-button"  > { props.register }  </button>
            </form>
        </div>
      ); 

}

export default Formlogin;
