import React, { useState, useEffect } from "react";

function TaskArea() {
    const [showForm, setShowForm] = useState(false);
    const [task, setTask] = useState({
        title: "",
        startDate: "",
        endDate: ""
    });
    const [taskList, setTaskList] = useState([]);  
    const [userId, setUserId] = useState(null);  // Estado para el ID del usuario autenticado

    // Función para alternar el formulario
    const handleToggleForm = () => {
        setShowForm(!showForm);
    };

    // Manejo de los cambios en los inputs
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setTask({
            ...task,
            [name]: value
        });
    };

    // Manejo del botón de cancelar
    const handleCancel = () => {
        setTask({
            title: "",
            startDate: "",
            endDate: ""
        });
        setShowForm(false);
    };

    // Función para obtener las tareas del usuario autenticado
    const fetchTasks = async () => {
        if (!userId) {
            console.error("User ID is not defined. Cannot fetch tasks.");
            return;
        }
        try {
            const response = await fetch(`http://localhost:4000/api/act-user/${userId}`);
            const data = await response.json();

            if (response.ok) {
                setTaskList(data);  // Guardar la lista de tareas en el estado
            } else {
                console.error("Failed to fetch tasks:", data);
            }
        } catch (error) {
            console.error("Error fetching tasks:", error);
        }
    };


    useEffect(() => {
        // Recuperar el userId del localStorage
        const storedUserId = localStorage.getItem("userId");

        // Verificar si existe el userId almacenado
        if (storedUserId) {
            setUserId(storedUserId);
        } else {
            console.error("No user ID found in localStorage");
        }
    }, []);

    useEffect(() => {
        if (userId) {
            fetchTasks();  // Llamar la función de tareas si el userId es válido
        } else {
            console.error("User ID is missing or undefined.");
        }
    }, [userId]);


    /*
    // useEffect para cargar el ID del usuario autenticado y las tareas al montar el componente
    useEffect(() => {
        // Obtener el userId del localStorage al montar el componente
        const storedUserId = localStorage.getItem("userId");  // Obtener el userId del localStorage
        console.log("Stored User ID:", storedUserId); 
        if (storedUserId) {
            setUserId(storedUserId);  // Actualizar el estado con el ID del usuario
        }
    }, []);

    // Llamar a fetchTasks cuando el userId cambie
    useEffect(() => {
        if (userId) {
            fetchTasks();  // Llamar la función para obtener las tareas cuando se tiene el userId
        }
    }, [userId]);*/

    return (
        <div className="task-container">
            <div className="addbutton-and-form">
                <span className="add-task" onClick={handleToggleForm}>Agregar nueva tarea +</span>
                <form className={`form-task ${showForm ? 'show' : ''}`}>
                    <input 
                        className="in-task"
                        type="text"
                        name="title"
                        placeholder="¿Qué vas a agendar?"
                        value={task.title}
                        onChange={handleInputChange}
                        required
                    />
                    <label>
                        Inicia: 
                        <input 
                            className="sh-date"
                            type="datetime-local"
                            name="startDate"
                            value={task.startDate}
                            onChange={handleInputChange}
                            required
                        />
                    </label>
                    <label>
                        Termina: 
                        <input 
                            className="sh-date"
                            type="datetime-local"
                            name="endDate"
                            value={task.endDate}
                            onChange={handleInputChange}
                            required
                        />
                    </label>
                    <button 
                        className="add-button"
                        type="submit"
                    >
                        Agregar
                    </button>
                    <button 
                        type="button"
                        className="cancel"
                        onClick={handleCancel}
                    >
                        Cancelar
                    </button>
                </form>
            </div>

            <div className="task-list">
                {taskList.length > 0 ? (
                    taskList.map((task, index) => (
                        <div key={index} className="task-item">
                            <p><strong></strong> {task.description}</p>
                            <p><strong>Inicia:</strong> {new Date(task.start_date).toLocaleString()}</p>
                            <p><strong>Termina:</strong> {new Date(task.end_date).toLocaleString()}</p>
                        </div>
                    ))
                ) : (
                    <p>No hay tareas pendientes</p>
                )}
            </div>
        </div>
    );
}

export default TaskArea;










