import React, { useState, useEffect } from "react";
import Swal from "sweetalert2";
import "./styles/FormRecoveryPassword.css";

function RecuperarContraseña({ email, onClose, initialRecoveryCode }) {
    const [enteredCode, setEnteredCode] = useState("");
    const [recoveryCode, setRecoveryCode] = useState(initialRecoveryCode); // Manejo interno del código de recuperación
    const [isCodeCorrect, setIsCodeCorrect] = useState(false);
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    // Verificar el código al ingresar los cuatro dígitos
    useEffect(() => {
        if (enteredCode.length === 4) {
            console.log("Código generado (actualizado):", recoveryCode);
            console.log("Código ingresado por el usuario:", enteredCode);

            if (enteredCode === recoveryCode) {
                setIsCodeCorrect(true);
                Swal.fire("Éxito", "Código correcto. Ahora puedes ingresar tu nueva contraseña.", "success");
            } else {
                setIsCodeCorrect(false);
                Swal.fire("Error", "Código incorrecto. Intenta de nuevo.", "error");
            }
        }
    }, [enteredCode, recoveryCode]);

    const handleCodeChange = (e) => {
        const value = e.target.value;
        if (/^\d{0,4}$/.test(value)) {
            setEnteredCode(value);
        }
    };

    /*const handleResendCode = async () => {
        try {
            const response = await fetch("http://localhost:4000/api/users/check-email", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ email }),
            });

            const data = await response.json();
            if (response.ok && data.exists) {
                const sendCodeResponse = await fetch("http://localhost:4000/api/users/send-recovery-code", {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({ email }),
                });

                if (sendCodeResponse.ok) {
                    const newCode = (await sendCodeResponse.json()).recoveryCode; // Recupera el nuevo código de la respuesta
                    setRecoveryCode(newCode); // Actualiza el código en el estado
                    Swal.fire("Código Reenviado", "Revisa tu correo para el nuevo código de recuperación.", "success");
                    console.log("nuevo codigo;", newCode);
                } else {
                    Swal.fire("Error", "No se pudo enviar el código. Intenta nuevamente.", "error");
                }
            } else {
                Swal.fire("Error", "El correo no está registrado.", "error");
            }
        } catch (error) {
            console.error("Error al reenviar el código:", error);
            Swal.fire("Error", "No se pudo reenviar el código. Intenta nuevamente.", "error");
        }
    };*/

    const handleResendCode = async () => {
        try {
            const response = await fetch("http://localhost:4000/api/users/send-recovery-code", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ email }),
            });

            const data = await response.json();
            if (response.ok) {
                const newCode = data.recoveryCode; // Recuperar el código generado
                setRecoveryCode(newCode); // Actualizar el estado con el nuevo código
                Swal.fire("Código Reenviado", "Revisa tu correo para el nuevo código de recuperación.", "success");
            } else {
                Swal.fire("Error", "No se pudo reenviar el código. Intenta nuevamente.", "error");
            }
        } catch (error) {
            console.error("Error al reenviar el código:", error);
            Swal.fire("Error", "No se pudo reenviar el código. Intenta nuevamente.", "error");
        }
    };    

    return (
        <div className="recovery">
            <form className="form-recovery" onSubmit={(e) => e.preventDefault()}>
                <h1>Recuperar Contraseña</h1>
                <p>Ingresa el código recibido</p>
                <input
                    type="number"
                    pattern="[0-9]{0,4}"
                    value={enteredCode}
                    onChange={handleCodeChange}
                    maxLength={4}
                    className="code"
                />
                <p>¿No has recibido tu código? Inténtalo de nuevo.</p>
                <button type="button" onClick={handleResendCode}>Reenviar código</button>
                {isCodeCorrect && (
                    <>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Nueva Contraseña"
                            className="pass"
                            required
                        />
                        <input
                            type="password"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            placeholder="Confirmar Contraseña"
                            className="pass"
                            required
                        />
                        <button type="submit">Cambiar Contraseña</button>
                        <button type="button" onClick={onClose}>Cancelar</button>
                    </>
                )}
            </form>
        </div>
    );
}

export default RecuperarContraseña;
