import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function UpdateUserForm({ userId, onClose }) {
    const [name, setName] = useState("");
    const [fullname, setFullname] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState(""); // Puede ser opcional
    const [isSubmitting, setIsSubmitting] = useState(false);
    const navigate = useNavigate();

    // **Obtener los datos del usuario al montar el componente**
    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await fetch(`http://localhost:4000/api/users/${userId}`);
                const data = await response.json();
                if (response.ok) {
                    // **Cargar los datos del usuario en los campos del formulario**
                    setName(data[0].name);
                    setFullname(data[0].fullname);
                    setEmail(data[0].email);
                    //setPassword(data[0].password);
                } else {
                    console.error("Error al obtener los datos del usuario:", data);
                    alert("Error al cargar la información del usuario");
                }
            } catch (error) {
                console.error("Error al conectar con el servidor:", error);
                alert("Error al conectar con el servidor");
            }
        };

        if (userId) fetchUserData(); // Solo obtener los datos si hay un userId válido
    }, [userId]);

    // **Manejar el envío del formulario para actualizar los datos**
    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);

        try {
            const updates = { name, fullname, email, password };
            const response = await fetch(`http://localhost:4000/api/users/${userId}`, {
                method: "PATCH",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(updates),
            });

            if (response.ok) {
                alert("Perfil actualizado exitosamente");
                const updatedUser = await response.json();
                localStorage.setItem("username", name); // **Actualizar el nombre en localStorage**
                onClose(); // **Cerrar el formulario**
                navigate("/diary"); // Redirigir a la pantalla principal
            } else {
                console.error("Error al actualizar el perfil:", response.status);
                alert("Error al actualizar el perfil");
            }
        } catch (error) {
            console.error("Error al actualizar el perfil:", error);
            alert("Error al conectar con el servidor");
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div className="update-user">
            <form className="form-update-user" onSubmit={handleSubmit}>
                <p> Actualizar perfil </p>
                <label>
                    Nombre:
                    <input
                        type="text"
                        value={name} // **Valor prellenado**
                        onChange={(e) => setName(e.target.value)}
                        placeholder="Nombres"
                        required
                    />
                </label>
                <label>
                    Apellido:
                    <input
                        type="text"
                        value={fullname} // **Valor prellenado**
                        onChange={(e) => setFullname(e.target.value)}
                        placeholder="Apellidos"
                        required
                    />
                </label>
                <label>
                    Correo:
                    <input
                        type="email"
                        value={email} // **Valor prellenado**
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Dirección de correo"
                        required
                    />
                </label>
                <label>
                    Clave:
                    <input
                        type="password"
                        value={password} // **Inicialmente vacío**
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Ingrese su nueva clave si desea cambiarla"
                    />
                </label>
                <button type="submit" disabled={isSubmitting}>
                    Actualizar
                </button>
                <button
                    onClick={onClose}
                    className="cancel-button"
                    type="button"
                >
                    Cancelar
                </button>
            </form>
        </div>
    );
}

export default UpdateUserForm;
