import React, { useState, useEffect } from "react";
import Swal from "sweetalert2";
import "./styles/FormRecoveryPassword.css";

function RecuperarContraseña({ email, onClose, recoveryCode }) {
    const [enteredCode, setEnteredCode] = useState("");
    const [isCodeCorrect, setIsCodeCorrect] = useState(false);
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    // Verificar el código al ingresar los cuatro dígitos
    useEffect(() => {
        if (enteredCode.length === 4) {
            if (enteredCode === recoveryCode) {
                setIsCodeCorrect(true);
                Swal.fire("Éxito", "Código correcto. Ahora puedes ingresar tu nueva contraseña.", "success");
            } else {
                setIsCodeCorrect(false);
                Swal.fire("Error", "Código incorrecto. Intenta de nuevo.", "error");
            }
        }
    }, [enteredCode, recoveryCode]);

    const handleCodeChange = (e) => {
        const value = e.target.value;
        if (/^\d{0,4}$/.test(value)) { // Asegura que solo se permiten números y máximo 4 caracteres
            setEnteredCode(value);
            console.log(setEnteredCode(value));
            console.log(recoveryCode);
        }
    };

    return (
        <div className="recovery">
            <form className="form-recovery" onSubmit={(e) => e.preventDefault()}>
                <h1>Recuperar Contraseña</h1>
                <input
                    type="number"
                    pattern="[0-9]{0,4}"
                    placeholder="Ingresa el código"
                    value={enteredCode}
                    onChange={handleCodeChange}
                    maxLength={4}
                />
                {isCodeCorrect && (
                    <>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Nueva Contraseña"
                            required
                        />
                        <input
                            type="password"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            placeholder="Confirmar Contraseña"
                            required
                        />
                        <button type="submit">Cambiar Contraseña</button>
                    </>
                )}
            </form>
        </div>
    );
}

export default RecuperarContraseña;
