import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import "./styles/FormUpdateUser.css";

function ChangePasswordForm({ userId, onClose }) {
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validar que las contraseñas coincidan
        if (password !== confirmPassword) {
            Swal.fire("Error", "Las contraseñas no coinciden. Intenta nuevamente.", "error");
            return;
        }

        // Enviar la solicitud para actualizar la contraseña
        try {
            const response = await fetch(`http://localhost:4000/api/users/${userId}`, {
                method: "PATCH",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ password }),
            });

            if (response.ok) {
                await Swal.fire("Éxito", "Tu contraseña ha sido actualizada correctamente.", "success");
                onClose(); // Cerrar el formulario
                navigate("/"); // Redirigir al inicio de sesión
            } else {
                Swal.fire("Error", "No se pudo actualizar la contraseña. Intenta nuevamente.", "error");
            }
        } catch (error) {
            console.error("Error al actualizar la contraseña:", error);
            Swal.fire("Error", "Error al conectar con el servidor. Intenta nuevamente.", "error");
        }
    };

    const handleCancel = () => {
        // Limpia los estados y cierra el formulario
        setPassword("");
        setConfirmPassword("");
        onClose();
    };

    return (
        <div className="overlay1">
            <div className="update-user">
                <form className="form-update-user" onSubmit={handleSubmit}>
                    <h2>Cambiar Contraseña</h2>
                    <label>
                        Nueva Contraseña:
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Ingrese su nueva contraseña"
                            required
                        />
                    </label>
                    <label>
                        Confirmar Contraseña:
                        <input
                            type="password"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            placeholder="Confirme su nueva contraseña"
                            required
                        />
                    </label>
                    <div className="button-group">
                        <button type="submit">Actualizar Contraseña</button>
                        <button type="button" onClick={handleCancel}>
                            Cancelar
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default ChangePasswordForm;
