// src/components/RegisterForm.js
import React, { useState } from 'react';

function RegisterForm({ onClose }) {
  const [name, setName] = useState('');
  const [fullname, setFullname] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleRegister = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError('');

    try {
      const response = await fetch('http://localhost:4000/api/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          name,
          fullname,
          email,
          password
        })
      });

      if (response.status === 409) {
        setError('The email address already exists. Please enter another email address or try reactivating your account from the login screen.');
        setIsSubmitting(false);
        return;
      }

      if (!response.ok) {
        const data = await response.json();
        setError(data.message);
        setIsSubmitting(false);
        return;
      }

      // Si el registro es exitoso, redirigir a la página del diario
      window.location.href = '/diary';
    } catch (error) {
      setError('Error during registration: ' + error.message);
      setIsSubmitting(false);
    }
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>&times;</span>
        <form onSubmit={handleRegister}>
          <h2>Register</h2>
          {error && <p className="error">{error}</p>}
          <label>
            Name:
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </label>
          <label>
            Fullname:
            <input
              type="text"
              value={fullname}
              onChange={(e) => setFullname(e.target.value)}
              required
            />
          </label>
          <label>
            Email:
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            {error.includes('The email address already exists') && <p className="error">{error}</p>}
          </label>
          <label>
            Password:
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </label>
          <button type="submit" disabled={!name || !fullname || !email || !password || isSubmitting}>
            Register
          </button>
          <button type="button" onClick={onClose}>
            Cancel
          </button>
        </form>
      </div>
    </div>
  );
}

export default RegisterForm;
