import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";




function UpdateUserForm({ userId, onClose }) {
    const [name, setName] = useState("");
    const [fullname, setFullname] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [isSubmitting, setIsSubmitting] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserData = async() => {
            try {
                const response = await fetch(`http://localhost:4000/api/users/${userId}`);
                const data = await response.json();
                if (response.ok) {
                    setName(data.name);
                    setFullname(data.fullname);
                    setEmail(data.email);
                } else {
                    console.error("Error fetching user data:", data);
                }
            } catch (error) {
                console.error("Error:", error);
            }
        };

        if (userId) fetchUserData();
    }, [userId]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);

        try {
            const updates = {name, fullname, email, password};
            const response = await fetch(`http://localhost:4000/api/users/${userId}`, {
                method: "PATCH",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(updates),
            });

            if (response.ok) {
                alert("Perfil actualizado exitosamente");
                const updatedUser = await response.json();
                localStorage.setItem("username", name); // Actualiza el nombre en localStorage
                onClose(); // Cierra el formulario
                navigate("/diary"); // Redirige a la pantalla de tareas
            } else {
                console.error("Error updating user:", response.status);
                alert("Error al actualizar el perfil");
            }

        } catch (error) {
            console.error("Error:", error);
            alert("Error al conectar con el servidor");
        } finally{
            setIsSubmitting(false);
        }
    };

    return (
        <div className="update-user">
            <form className="form-update-user">
                <p> Actualizar perfil </p>
                <label>
                    Nombre:
                    <input
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    placeholder='Nombres'
                    required
                    />
                </label>
                <label>
                    Apellido:
                    <input
                    type="text"
                    value={fullname}
                    onChange={(e) => setFullname(e.target.value)}
                    placeholder='Apellidos'
                    required
                    />
                </label>
                <label>
                    Correo:
                    <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder='DirecciÃ²n de Correo'
                    required
                    />
                </label>
                <label>
                    Clave:
                    <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder='Clave de acceso'
                    required
                    />
                </label>
                <button type="submit" disabled={isSubmitting}>
                    Actualizar
                </button>
                <button
                    onClick={() => navigate("/")}
                    className='cancel-button'
                    type='button'
                >
                    Cancelar
                </button>
            </form>
        </div>
    )
};

export default UpdateUserForm;
