import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";

function Formlogin(props) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleCheckActiveStatus = async () => {
    if (!email) return;

    try {
        const response = await fetch(`http://localhost:4000/api/users/check-active`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ email }), // Enviando correctamente el email
        });

        const data = await response.json();
        if (!response.ok) {
            console.error(data.message);
            return;
        }

        if (!data.active) {
            const result = await Swal.fire({
                title: "Su cuenta está desactivada",
                text: "¿Desea activarla nuevamente?",
                icon: "warning",
                showCancelButton: true,
                confirmButtonText: "Sí",
                cancelButtonText: "No",
            });

            if (result.isConfirmed) {
                const activateResponse = await fetch(`http://localhost:4000/api/users/activate`, {
                    method: "PATCH",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({ email }), // Asegurarse de enviar el email correctamente
                });

                const activateData = await activateResponse.json();
                if (activateResponse.ok) {
                    Swal.fire("Cuenta activada", "Su cuenta ha sido reactivada. Para acceder, ingrese sus credenciales", "success");
                } else {
                    Swal.fire("Error", "Error al activar la cuenta.", "error");
                }
            }
        }
    } catch (error) {
        console.error("Error al verificar el estado de la cuenta:", error);
        Swal.fire("Error", "Error al verificar el estado de la cuenta.", "error");
    }
};


  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:4000/api/users/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();
      if (response.ok) {
        // Guardar el token en localStorage o context
        localStorage.setItem("token", data.token);
        localStorage.setItem("userId", data.user.id); 
        localStorage.setItem("username", data.user.name);
        // Redirigir a la nueva página
        navigate("/diary");
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("Error during login:", error);
      alert("An error occurred. Please try again later.");
    }
  };

  return (
    <div className="content-login">
      <form className="form-login" onSubmit={handleLogin}>
        <h1 className="title"> Comienza a planificar tu día </h1>
        <p>{props.message}</p>
        <label className="label-user" htmlFor="email">{props.email}</label>
        <input 
          className="in-email" 
          name="email" 
          type="text" 
          placeholder="Email de usuario" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)}
          onBlur={handleCheckActiveStatus} // Agregar onBlur
        />
        <label className="label-password" htmlFor="password">{props.pass}</label>
        <input 
          className="in-password" 
          name="password" 
          type="password" 
          placeholder="User Password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
        />
        <button className="login-button" type="submit">{props.login}</button>
        <p className="regmes">
          {props.messageReg}
          <button 
            className="register-button" 
            type="button" 
            onClick={() => navigate("/register")}
          >
            {props.register}
          </button>
        </p>
      </form>
    </div>
  );
}

export default Formlogin;
