import React, { useState, useEffect } from "react";
import { Tooltip, TooltipProvider } from "react-tooltip";
import 'react-tooltip/dist/react-tooltip.css';

function TaskArea() {
    const [showForm, setShowForm] = useState(false);
    const [task, setTask] = useState({
        description: "",  // Cambié 'title' a 'description'
        startDate: "",
        endDate: ""
    });
    const [taskList, setTaskList] = useState([]);  
    const [userId, setUserId] = useState(null);  // Estado para el ID del usuario autenticado

    // Función para alternar el formulario
    const handleToggleForm = () => {
        setShowForm(!showForm);
    };

    // Manejo de los cambios en los inputs
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setTask({
            ...task,
            [name]: value
        });
    };

    // Manejo del botón de cancelar
    const handleCancel = () => {
        setTask({
            description: "",
            startDate: "",
            endDate: ""
        });
        setShowForm(false);
    };

    // Función para enviar la tarea (POST)
    const handleSubmit = async (e) => {
        e.preventDefault();  // Evita el comportamiento por defecto del formulario

        if (!userId) {
            console.error("User ID is not defined.");
            return;
        }

        const newTask = {
            description: task.description,
            start_date: task.startDate,
            end_date: task.endDate,
            user_id: userId  // Incluye el user_id del usuario logeado
        };

        try {
            const response = await fetch("http://localhost:4000/api/activities", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(newTask)
            });

            if (response.ok) {
                const result = await response.json();
                console.log("Tarea añadida:", result);
                // Actualizar la lista de tareas después de añadir la nueva tarea
                fetchTasks();
                // Limpiar el formulario
                handleCancel();
            } else {
                console.error("Failed to add task:", response.status);
            }
        } catch (error) {
            console.error("Error while adding task:", error);
        }
    };

    // Función para obtener las tareas del usuario autenticado
    const fetchTasks = async () => {
        if (!userId) {
            console.error("User ID is not defined. Cannot fetch tasks.");
            return;
        }
        try {
            const response = await fetch(`http://localhost:4000/api/act-user/${userId}`);
            const data = await response.json();

            if (response.ok) {
                setTaskList(data);  // Guardar la lista de tareas en el estado
            } else {
                console.error("Failed to fetch tasks:", data);
            }
        } catch (error) {
            console.error("Error fetching tasks:", error);
        }
    };


    useEffect(() => {
        // Recuperar el userId del localStorage
        const storedUserId = localStorage.getItem("userId");

        // Verificar si existe el userId almacenado
        if (storedUserId) {
            setUserId(storedUserId);
        } else {
            console.error("No user ID found in localStorage");
        }
    }, []);

    useEffect(() => {
        if (userId) {
            fetchTasks();  // Llamar la función de tareas si el userId es válido
        } else {
            console.error("User ID is missing or undefined.");
        }
    }, [userId]);

    return (
        <TooltipProvider>
        <div className="task-container">
            <div className="addbutton-and-form">
                <span className="add-task" onClick={handleToggleForm}>Agregar nueva tarea +</span>
                <form className={`form-task ${showForm ? 'show' : ''}`} onSubmit={handleSubmit}>
                    <input 
                        className="in-task"
                        type="text"
                        name="description"
                        placeholder="¿Qué vas a agendar?"
                        value={task.description}
                        onChange={handleInputChange}
                        required
                    />
                    <label>
                        Inicia: 
                        <input 
                            className="sh-date"
                            type="datetime-local"
                            name="startDate"
                            value={task.startDate}
                            onChange={handleInputChange}
                            required
                        />
                    </label>
                    <label>
                        Termina: 
                        <input 
                            className="sh-date"
                            type="datetime-local"
                            name="endDate"
                            value={task.endDate}
                            onChange={handleInputChange}
                            required
                        />
                    </label>
                    <button 
                        className="add-button"
                        type="submit"
                    >
                        Agregar
                    </button>
                    <button 
                        type="button"
                        className="cancel"
                        onClick={handleCancel}
                    >
                        Cancelar
                    </button>
                </form>
            </div>

            <div className="task-list">
                {taskList.length > 0 ? (
                    taskList.map((task, index) => (
                        <div key={index} className="task-item">
                            <p className="desctask"> {task.description}</p>
                            <p className="horafecha"><strong>Desde:</strong> {new Date(task.start_date).toLocaleString()}</p>
                            <p className="horafecha"><strong>Hasta:</strong> {new Date(task.end_date).toLocaleString()}</p>
                            <div className="botones">
                                <span className="material-symbols-outlined" role="button" data-tooltip-id="complete-tooltip" data-tooltip-content="Completar Tarea">
                                    done_all
                                </span>
                                <span className="material-symbols-outlined" role="button" data-tooltip-id="edit-tooltip" data-tooltip-content="Editar Tarea">
                                    edit_note
                                </span>
                                <span  className="material-symbols-outlined" role="button" data-tooltip-id="delete-tooltip" data-tooltip-content="Eliminar Tarea">
                                    delete
                                </span>
                            </div>
                        </div>
                    ))
                ) : (
                    <p className="no-task">No hay tareas pendientes</p>
                )}
            </div>
            <Tooltip id="complete-tooltip" />
            <Tooltip id="edit-tooltip" />
            <Tooltip id="delete-tooltip" />
        </div>
    </TooltipProvider>
    );
}

export default TaskArea;
