import "./styles/FormSendEmail.css";
import React, { useState } from "react";
import { Navigate } from "react-router-dom";
import Swal from "sweetalert2";
import RecoveryPassword from "./FormRecoveryPassword";
import e from "express";



function FormSendEmail({onClose}) {
    const [email, setEmail] = useState("");
    const [showRecoveryPassword, setShowRecoveryPassword] = useState(false);


    const handleSendEmail= async()=> {
        try {
            const response = await fetch("http://localhost:4000/api/users/send-recovery-code", {
                method: "POST",
                headers: {"Content-Type": "application/jsosn"},
                body: JSON.stringify({email}),
            });

            const data = response.json();
            if (response.ok) {
                Swal.fire["Correo Enviado", "Revisa tu bandeja de entrada para visualizar el còdigo de recuperaciòn de contraseña."];
                setShowRecoveryPassword[true];
            } else {
                Swal();
            }
        } catch (error) {
            console.error("Error al enviar el correo: ", error);
            Swal.fire("Error", "No es posible enviar el correo. Intentalo mas tarde", "error");
        }
    };

    return (
        <div className="send">
            {!showRecoveryPassword ? (
                <form 
                    className="formsend"
                    onScroll={(e) => {e.preventDefault(); handleSendEmail();}}
                    >
                    <h1> Recuperar Contraseña </h1>
                    <p> Recibiràs un còdigo de veriicaciòn de cuatro dìgitos. </p>
                    <input
                        type="email"
                        placeholder="Correo de usuario"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <button
                        type="submit"
                    >
                        Enviiar 
                    </button>
                    <button
                        onClick={onClose}
                    >
                        Cancelar
                    </button>
             </form>
            ) : (
                <RecoveryPassword email={email} onClose={onClose}  />
            )}
        </div>
    )
};

export default FormSendEmail;
