import React, { useState } from "react";
import Swal from "sweetalert2";
import RecuperarContraseña from "./FormRecoveryPassword"; // Asegúrate de tener este componente
import "./styles/FormSendEmail.css";

function FormSendEmail({ onClose }) {
    const [email, setEmail] = useState("");
    const [showRecuperarContraseña, setShowRecuperarContraseña] = useState(false);

    const handleSendEmail = async () => {
        try {
            console.log("Verificando si el correo existe:", email); // Depuración
            const response = await fetch("http://localhost:4000/api/users/check-email", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ email }),
            });

            const data = await response.json();
            console.log("Respuesta de check-email:", data); // Depuración

            if (response.ok) {
                if (data.exists) {
                    console.log("El correo existe, enviando código de recuperación."); // Depuración
                    const sendCodeResponse = await fetch("http://localhost:4000/api/users/send-recovery-code", {
                        method: "POST",
                        headers: { "Content-Type": "application/json" },
                        body: JSON.stringify({ email }),
                    });

                    if (sendCodeResponse.ok) {
                        console.log("Código de recuperación enviado."); // Depuración
                        Swal.fire("Correo Enviado", "Revisa tu correo para el código de recuperación.", "success");
                        setShowRecuperarContraseña(true);
                    } else {
                        Swal.fire("Error", "No se pudo enviar el correo. Intenta nuevamente.", "error");
                    }
                } else {
                    console.log("El correo no está registrado."); // Depuración
                    Swal.fire("Error", "El correo no está registrado.", "error");
                }
            } else {
                Swal.fire("Error", "Error al verificar el correo.", "error");
            }
        } catch (error) {
            console.error("Error al verificar el correo:", error);
            Swal.fire("Error", "No se pudo verificar el correo. Intenta nuevamente.", "error");
        }
    };

    return (
        <div className="send">
            {!showRecuperarContraseña ? (
                <form className="formsend" onSubmit={(e) => { e.preventDefault(); handleSendEmail(); }}>
                    <h1>Enviar correo</h1>
                    <p>Se enviará un código de recuperación a tu bandeja de entrada.</p>
                    <input
                        type="email"
                        placeholder="Ingresa tu correo"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <button type="submit">Enviar</button>
                    <button type="button" onClick={onClose}>Cancelar</button>
                </form>
            ) : (
                <RecuperarContraseña email={email} onClose={onClose} />
            )}
        </div>
    );
}

export default FormSendEmail;
