//import e from "express";
import  React, { useState } from "react";
//import { IoReturnUpBackSharp } from "react-icons/io5";

function RegisterForm ({ onClose }) {
    const [name, setName] = useState("");
    const [fullname, setFullname] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [isSubmitting, setIsSubmitting] = useState(false);

    const handleRegister = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);
        setError("");

        try {
            const response = await fetch("http://localhost:4000/api/users", {
                method: "POST",
                headers: {
                    "Content_Type": "application/json"
                },
                body: JSON.stringify({
                    name,
                    fullname,
                    email,
                    password
                })
            });

            if (!response) {
                const data = await response.json();
                if (data.message === "All fields are required") {
                    setError("All fields are required");
                }else if (response.status===400) {
                    setError("The email address already exists. Please enter another email address or try reactivating your account from the login screen.");
                } else {
                    setError("An unexpected error occurred. Please try again.");
                }
                setIsSubmitting(false);
                return;
            }
            window.location.href("/diary");                 
        } catch (error) {
            setError("Error during registration" + error.message);
            setIsSubmitting(false);
        }
    };

    return(
        <div className="modal">
            <div>
                <span className="close" onClick={onClose}>&times;</span>
                <form onSubmit={handleRegister}>
                    <h2>Register</h2>
                    {error && <p className="error"> {error} </p>}
                    <label>
                        Name:
                        <input 
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            required
                        /> 
                    </label>
                    <label>
                        Fullname:
                        <input 
                            type="texte"
                            value={fullname}
                            onChange={(e) => setFullname(e.target.value)}
                            required
                        />
                    </label>
                    <label>
                        Email;
                        <input 
                            type="text"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </label>
                    <label>
                        Password:
                        <input 
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </label>
                    <button type="submit" disabled={!name || !fullname || !email || !password || isSubmitting}>
                        Register
                    </button>
                    <button type="button" onClick={onclose} >
                        Cancel
                    </button>
                </form>
            </div>
        </div>
    )
};

export default RegisterForm;
