import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./styles/Diary.css";

function Diary() {
    const navigate = useNavigate();
    const [showMenu, setShowMenu] = useState(false);
    const [userName, setUserName] = useState("");

    //obtener nombre de usuario
    useEffect(()=> {
        const username = localStorage.getItem("username");
        if (username) {
            setUserName(username)
        } else {
            navigate("/")
        }
    }, [navigate]);

    const handleLogout = async () => {
        try {
            const response = await fetch("http://localhost:4000/api/users/logout", {
                method: "GET",
                credentials: "include"  // Esto incluye las cookies en la solicitud
            });

            if (response.ok) {
                localStorage.removeItem("token");
                localStorage.removeItem("username");
                navigate("/");
            } else {
                console.error("Logout failed");
            }
        } catch (error) {
            console.error("Error during logout process", error);
        }
    };

    const toggleMenu = ()=> {
        setShowMenu(!showMenu);
    }

    return (
        <div>
            <header className="encabezado" >
                <div className="user-info">
                    <span onClick={toggleMenu} className="user-name">
                        { userName }
                        <span className="dropdown-icon">▼</span>
                    </span>
                    {showMenu && (
                        <div className="dropdown-menu">
                            <ul>
                                <li>Editar perfil</li>
                                <li>Limpiar lista de Tareas</li>
                                <li>Eliminar cuenta</li>
                            </ul>
                        </div>
                    )}
                </div>
                <button className="boton-cs" onClick={handleLogout}>
                    Cerrar sesiòn
                </button>
            </header>
            <main>
                <h1>Bienvenido a tu agenda </h1>
            </main>
        </div>
    );
}

export default Diary;
