import React, { useState } from "react";
import { RxValue } from "react-icons/rx";

function TaskArea() {
    const [showForm, setShowForm] = useState(false);
    const [task, showTask] = useState({
        tittle: "",
        startDate: "",
        endDate: ""
    });

    const  handleToggleForm = ()=> {
        setShowForm(!showForm);
    };

    const handleInputChange = (e)=> {
        const { name, value} = e.target;
        setTask({
            ...task,
            [name]: value
        });
    };

    const handleICancel = ()=> {
        setTask({
            title: "",
            startDate: "",
            endDate: ""
        });
        setShowForm(false);
    };

    return(
        <div className="task-container">
            <div className="addbutton-and-form">
                <span className="add-task">Agregar nueva tarea +</span>
                <form className="form-task">
                    <input 
                        className="in-task"
                        type="text"
                        placeholder="¿Què vas a agendar?"
                        required
                    />
                    <label>
                        Inicia: 
                        <input 
                            className="sh-date"
                            type="datetime-local"
                            placeholder="dd/mm/aaaa, hh:mm am-pm"
                            required
                        />
                    </label>
                    <label>
                        Termina: 
                        <input 
                            className="sh-date"
                            type="datetime-local"
                            placeholder="dd/mm/aaaa, hh:mm am-pm"
                            required
                        />
                    </label>
                    <button 
                        className="add-button"
                        type="submit"
                    >
                        Agregar
                    </button>
                    <button className="cancel">Cancelar</button>
                </form>
            </div>
        </div>
    )
}

export default TaskArea;
