import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./styles/FormUpdateUser.css"; // Asegúrate de agregar los estilos aquí.
import Swal from "sweetalert2";

function ChangePasswordForm({ userId, onClose }) {
    //const [name, setName] = useState("");
    //const [fullname, setFullname] = useState("");
    //const [email, setEmail] = useState("");
    const [password, setPassword] = useState(""); // Puede ser opcional
    const [confirmPassword, setConfirmPassword] = useState("");
    //const [isSubmitting, setIsSubmitting] = useState(false);
    const navigate = useNavigate();

    // **Obtener los datos del usuario al montar el componente**
    /*useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await fetch(`http://localhost:4000/api/users/${userId}`);
                const data = await response.json();
                if (response.ok && data.length > 0) {
                    setName(data[0].name);
                    setFullname(data[0].fullname);
                    setEmail(data[0].email);
                    //setPassword(data[0].password);
                } else {
                    console.error("Error al obtener los datos del usuario:", data);
                    alert("No se pudo cargar la información del usuario");
                }
            } catch (error) {
                console.error("Error al conectar con el servidor:", error);
                alert("Error al conectar con el servidor");
            }
        };

        if (userId) fetchUserData(); // Solo obtener los datos si hay un userId válido
    }, [userId]);*/

    // **Manejar el envío del formulario para actualizar los datos**

    const handleSubmit = async (e) => {
        e.preventDefault(); // Evita el comportamiento predeterminado del formulario

        // Validar que ambas contraseñas coincidan
        if (password !== confirmPassword) {
            Swal.fire("Error", "Las contraseñas no coinciden. Intenta nuevamente.", "error");
            return; // Detén la ejecución si las contraseñas no coinciden
        }

        try {
            const updates = { password };
            const response = await fetch(`http://localhost:4000/api/users/${userId}`, {
                method: "PATCH",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(updates),
            });

            if (response.ok) {
                await Swal.fire("Éxito", "Tu contraseña ha sido actualizada correctamente.", "success");
                onClose(); // Cierra el formulario al completar
            } else {
                Swal.fire("Error", "No se pudo actualizar la contraseña. Intenta nuevamente.", "error");
            }
        } catch (error) {
            console.error("Error al actualizar la contraseña:", error);
            Swal.fire("Error", "Error al conectar con el servidor. Intenta nuevamente.", "error");
        }
    };

    const handleClose = (e) => {
        e.preventDefault(); // Evita la validación o cualquier acción relacionada al formulario
        setPassword(""); // Limpia el estado de contraseña
        setConfirmPassword(""); // Limpia el estado de confirmación
        onClose(); // Cierra el formulario
    };


    /*const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);

        try {
            const updates = { name, fullname, email, password };
            const response = await fetch(`http://localhost:4000/api/users/${userId}`, {
                method: "PATCH",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(updates),
            });

            if (response.ok) {
                alert("Perfil actualizado exitosamente");
                //await Swal.fire("Listo", "Perfil actualizado", "success");
                const updatedUser = await response.json();
                localStorage.setItem("username", name);
                onClose(); // Cerrar el formulario
                navigate("/diary"); // Redirigir a la pantalla principal
            } else {
                console.error("Error al actualizar el perfil:", response.status);
                alert("Error al actualizar el perfil");
            }
        } catch (error) {
            console.error("Error al actualizar el perfil:", error);
            alert("Error al conectar con el servidor");
        } finally {
            setIsSubmitting(false);
        }
    };*/

    return (
        <div className="overlay1"> {/* Superposición para desactivar el resto de la pantalla */}
            <div className="update-user">
                <form className="form-update-user" onSubmit={handleSubmit}>
                    <p>Actualizar contraseña</p>
                    <label>
                        Contraseña:
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Nueva contraseña"
                            required
                        />
                    </label>
                    <label>
                        Confirmar contraseña:
                        <input
                            type="Password"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            placeholder="Vuelva a ingresar la contraseña"
                            required
                        />
                    </label>

                    <button 
                    type="submit" 
                    >
                        Actualizar contraseña
                    </button>
                    <button
                        onClick={handleClose}
                        className="cancel-button"

                    >
                        Cancelar
                    </button>
                </form>
            </div>
        </div>
    );
}

export default ChangePasswordForm;
