import React, { useState } from "react";

function TaskArea() {
    const [showForm, setShowForm] = useState(false);
    const [task, setTask] = useState({
        tittle: "",
        startDate: "",
        endDate: ""
    });

    const  handleToggleForm = ()=> {
        setShowForm(true);
    };

    const handleInputChange = (e)=> {
        const { name, value} = e.target;
        setTask({
            ...task,
            [name]: value
        });
    };

    const handleICancel = ()=> {
        setTask({
            title: "",
            startDate: "",
            endDate: ""
        });
        setShowForm(false);
    };

    return(
        <div className="task-container">
            <div className="addbutton-and-form">
                <span className="add-task" onClick={handleToggleForm}>Agregar nueva tarea +</span>
                {showForm && (
                    <form className="form-task">
                        <input 
                            className="in-task"
                            type="text"
                            placeholder="¿Què vas a agendar?"
                            name="title"
                            value={task.tittle}
                            onChange={handleInputChange}
                            required
                        />
                        <label>
                            Inicia: 
                            <input 
                                className="sh-date"
                                type="datetime-local"
                                placeholder="dd/mm/aaaa, hh:mm am-pm"
                                name="startDate"
                                value={task.startDate}
                                onChange={handleInputChange}
                                required
                            />
                        </label>
                        <label>
                            Termina: 
                            <input 
                                className="sh-date"
                                type="datetime-local"
                                placeholder="dd/mm/aaaa, hh:mm am-pm"
                                name="endDate"
                                value={task.endDate}
                                onChange={handleInputChange}
                                required
                            />
                        </label>
                        <button 
                            className="add-button"
                            type="submit"
                        >
                            Agregar
                        </button>
                        <button className="cancel" onClick={handleICancel} >Cancelar</button>
                    </form>
                )}
            </div>
        </div>
    )
}

export default TaskArea;
