import React, { useState } from "react";
import Swal from "sweetalert2";
import "./styles/FormRecoveryPassword.css";
function RecuperarContraseña({ email, onClose }) {
    const [code, setCode] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    const handleVerifyCode = () => {
        if (code === "tu_codigo_almacenado") {  // Sustituir por la lógica real de comparación
            Swal.fire("Código correcto", "Puedes ingresar tu nueva contraseña.", "success");
        } else {
            Swal.fire("Error", "El código ingresado es incorrecto.", "error");
        }
    };

    return (
        <div className="recovery">

            <form className="form-recovery" onSubmit={(e) => { e.preventDefault(); handleVerifyCode(); }}>
                <h1>Recuperar Contraseña</h1>
                <input
                    type="text"
                    placeholder="Ingresa el código"
                    value={code}
                    onChange={(e) => setCode(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Nueva contraseña"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    disabled={code !== "tu_codigo_almacenado"} // Deshabilitado hasta que el código sea correcto
                />
                <input
                    type="password"
                    placeholder="Confirma nueva contraseña"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    disabled={code !== "tu_codigo_almacenado"} // Deshabilitado hasta que el código sea correcto
                />
                <button type="submit">Recuperar</button>
                <button type="button" onClick={onClose}>Cancelar</button>
            </form>
        </div>
    );
}

export default RecuperarContraseña;
