            [name]: value
        });
    };

    // Manejo del botón de cancelar
    const handleCancel = () => {
        setTask({
            description: "",
            startDate: "",
            endDate: ""
        });
        setShowForm(false);
        setIsEditing(false);  // Resetear el estado de edición
