import React, { useState, useEffect } from "react";

function TaskArea() {
    const [showForm, setShowForm] = useState(false);
    const [task, setTask] = useState({
        title: "",
        startDate: "",
        endDate: ""
    });

    const [taskList, setTaskList] = useState([]);
    const [userId, setUserId] = useState(null);

    const handleToggleForm = () => {
        setShowForm(!showForm);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setTask({
            ...task,
            [name]: value
        });
    };

    const handleCancel = () => {
        setTask({
            title: "",
            startDate: "",
            endDate: ""
        });
        setShowForm(false);
    };

     // Función para obtener las tareas del usuario autenticado
     const fetchTask = async () => {
        if (!userId) return;
        try {
            const  response = await fetch(`http://localhost:3000/api/act-user/${userId}`);
            const data = await response.json();
            setTaskList(data);
        } catch (error) {
            console.error("Error fetching tasks:", error);
        }
     };



    return (
        <div className="task-container">
            <div className="addbutton-and-form">
                <span className="add-task" onClick={handleToggleForm}>Agregar nueva tarea +</span>
                <form className={`form-task ${showForm ? 'show' : ''}`}>
                    <input 
                        className="in-task"
                        type="text"
                        name="title"
                        placeholder="¿Qué vas a agendar?"
                        value={task.title}
                        onChange={handleInputChange}
                        required
                    />
                    <label>
                        Inicia: 
                        <input 
                            className="sh-date"
                            type="datetime-local"
                            name="startDate"
                            value={task.startDate}
                            onChange={handleInputChange}
                            required
                        />
                    </label>
                    <label>
                        Termina: 
                        <input 
                            className="sh-date"
                            type="datetime-local"
                            name="endDate"
                            value={task.endDate}
                            onChange={handleInputChange}
                            required
                        />
                    </label>
                    <button 
                        className="add-button"
                        type="submit"
                    >
                        Agregar
                    </button>
                    <button 
                        type="button"
                        className="cancel"
                        onClick={handleCancel}
                    >
                        Cancelar
                    </button>
                </form>
            </div>
            <div className="task-list">

            </div>
        </div>
    );
}

export default TaskArea;
