import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import "./styles/FormUpdateUser.css";

function ChangePasswordForm({ userId, onClose }) {
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const navigate = useNavigate();

    // **Validar contraseñas y enviar cambios**
    const handleSubmit = async (e) => {
        e.preventDefault(); // Evita la recarga de la página

        // Validar que ambas contraseñas coincidan
        if (password !== confirmPassword) {
            console.log("Contraseñas no coinciden"); // Depuración
            await Swal.fire("Error", "Las contraseñas no coinciden. Intenta nuevamente.", "error");
            return; // Detener flujo si las contraseñas no coinciden
        }

        console.log("Contraseñas coinciden, enviando solicitud..."); // Depuración

        try {
            const response = await fetch(`http://localhost:4000/api/users/${userId}`, {
                method: "PATCH",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ password }),
            });

            if (response.ok) {
                console.log("Contraseña actualizada correctamente"); // Depuración
                await Swal.fire("Éxito", "Tu contraseña ha sido actualizada correctamente.", "success");
                setPassword(""); // Limpiar estado
                setConfirmPassword("");
                onClose(); // Cerrar el formulario
            } else {
                console.error("Error al actualizar contraseña: ", response.status); // Depuración
                await Swal.fire("Error", "No se pudo actualizar la contraseña. Intenta nuevamente.", "error");
            }
        } catch (error) {
            console.error("Error al conectar con el servidor:", error); // Depuración
            await Swal.fire("Error", "Error al conectar con el servidor. Intenta nuevamente.", "error");
        }
    };

    // **Cerrar formulario**
    const handleClose = () => {
        console.log("Cerrando formulario..."); // Depuración
        setPassword(""); // Limpiar estado
        setConfirmPassword("");
        onClose(); // Cerrar el formulario
    };

    return (
        <div className="overlay1">
            <div className="update-user">
                <form className="form-update-user" onSubmit={handleSubmit}>
                    <h2>Cambiar Contraseña</h2>
                    <label>
                        Nueva Contraseña:
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Ingrese su nueva contraseña"
                            required
                        />
                    </label>
                    <label>
                        Confirmar Contraseña:
                        <input
                            type="password"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            placeholder="Confirma tu nueva contraseña"
                            required
                        />
                    </label>
                    <div className="button-group">
                        <button type="submit">Actualizar Contraseña</button>
                        <button type="button" onClick={handleClose}>
                            Cancelar
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default ChangePasswordForm;
